SELECT LPAD('ㄴ', LVL*4, ' ') || A.MENU_NM AS MENU_LVL , A.MENU_NM , A.LIC_GRP_ID , A.MENU_ID , A.UPPER_MENU_ID , SF_ADM1000_MENU_NM(A.LIC_GRP_ID, A.MENU_ID, '2') AS UP_MENU_NM , (SELECT UPPER_MENU_ID FROM ADM1000 Z WHERE Z.LIC_GRP_ID = A.LIC_GRP_ID AND Z.MENU_ID = A.UPPER_MENU_ID) AS TWO_UPPER_MENU_ID , SF_ADM1000_MENU_NM(A.LIC_GRP_ID, A.MENU_ID, '3') AS UPUP_MENU_NM , A.LVL , A.ORD , A.USE_CD , A.MENU_DESC , A.MENU_PATH , A.MENU_TYPE_CD , A.MENU_URL , A.MENU_IMG_URL , '' AS ROW_STATUS , A.MODULE_TYPE FROM ( SELECT * FROM ADM1000 WHERE LIC_GRP_ID = #licGrpId#
AND (MENU_ID < '000900000000' OR MENU_ID >= '001000000000')
) A WHERE 1=1 START WITH A.MENU_ID = '000' CONNECT BY PRIOR A.MENU_ID = A.UPPER_MENU_ID ORDER BY ORD ASC
