sqlMap.select:
SELECT B.PRJ_ID, C.PRJ_NM ,B.PROCESS_ID, B.PROCESS_NM ,COALESCE(B.PROCESS_DESC , ' ') AS PROCESS_DESC ,B.PROCESS_CONFIRM_CD ,CLOB_TO_CHAR(B.PROCESS_JSON_DATA) AS PROCESS_JSON_DATA ,B.USE_CD ,B.PROCESS_ORD ,TO_CHAR(B.REG_DTM, 'YYYY-MM-DD HH24:MI:SS') AS REG_DTM ,B.REG_USR_ID, B.REG_USR_IP ,TO_CHAR(B.MODIFY_DTM, 'YYYY-MM-DD HH24:MI:SS') AS MODIFY_DTM ,B.MODIFY_USR_ID, B.MODIFY_USR_IP FROM ( SELECT DISTINCT Z.PRJ_ID FROM ADM1100 Z INNER JOIN ADM1300 X ON ( Z.PRJ_ID = X.PRJ_ID AND Z.AUTH_GRP_ID = X.AUTH_GRP_ID ) WHERE 1=1 AND Z.USR_TYP = '02' AND Z.LIC_GRP_ID = #licGrpId#
AND USR_ID = #usrId#
) A INNER JOIN FLW1000 B ON (A.PRJ_ID = B.PRJ_ID ) INNER JOIN PRJ1000 C ON (B.PRJ_ID = C.PRJ_ID ) WHERE 1=1 AND B.USE_CD = '01' AND B.PROCESS_CONFIRM_CD = '02' ORDER BY B.PRJ_ID, B.PROCESS_ORD
