sqlMap.select:
SELECT T1.PRJ_ID , T1.PRJ_NM , T1.AUTH_GRP_ID , T1.AUTH_GRP_NM , COALESCE(T1.AUTH_GRP_DESC, ' ') AS AUTH_GRP_DESC , T1.CREATE_DT , T1.USE_CD , T1.ORD , T1.USR_TYP , SF_ADM4100_COM_CD_INFO(#licGrpId#, 'ADM00004', T1.USR_TYP, '1') AS USR_TYP_NM , SF_ADM4100_COM_CD_INFO(#licGrpId#, 'CMM00001', T1.ACCEPT_USE_CD, '1') AS ACCEPT_USE_NM FROM ( SELECT B.PRJ_ID , C.PRJ_NM , B.AUTH_GRP_ID , B.AUTH_GRP_NM , COALESCE(B.AUTH_GRP_DESC, ' ') AS AUTH_GRP_DESC , B.CREATE_DT , B.USE_CD , B.ORD , B.USR_TYP , B.ACCEPT_USE_CD FROM ( SELECT DISTINCT Z.PRJ_ID FROM ADM1100 Z INNER JOIN ADM1300 X ON ( Z.PRJ_ID = X.PRJ_ID AND Z.AUTH_GRP_ID = X.AUTH_GRP_ID ) WHERE 1=1 AND Z.USR_TYP = '02' AND Z.LIC_GRP_ID = #licGrpId#
AND USR_ID = #usrId#
) A INNER JOIN ADM1100 B ON ( A.PRJ_ID = B.PRJ_ID ) INNER JOIN PRJ1000 C ON ( A.PRJ_ID = C.PRJ_ID ) WHERE 1=1 AND C.USE_CD = '01' AND B.USE_CD = '01' ) T1 ORDER BY T1.PRJ_ID, T1.ORD
