sqlMap.select:
SELECT A.PRJ_ID, A.PROCESS_ID, A.FLOW_ID, A.FLOW_NEXT_ID ,A.FLOW_NM, A.FLOW_ESSENTIAL_CD, A.FLOW_SIGN_CD, A.FLOW_END_CD ,A.FLOW_WORK_CD ,A.FLOW_TITLE_BG_COLOR, A.FLOW_TITLE_COLOR, A.FLOW_CONTENT_BG_COLOR, A.FLOW_CONTENT_COLOR ,A.OPT_CNT ,NVL(A.REQ_TOTAL_CNT,0) AS REQ_TOTAL_CNT ,NVL(A.REQ_CHARGER_CNT,0) AS REQ_CHARGER_CNT ,TO_CHAR(A.REG_DTM, 'YYYY-MM-DD HH24:MI:SS') AS REG_DTM ,A.REG_USR_ID, A.REG_USR_IP ,TO_CHAR(A.MODIFY_DTM, 'YYYY-MM-DD HH24:MI:SS') AS MODIFY_DTM ,A.MODIFY_USR_ID, A.MODIFY_USR_IP FROM ( SELECT B.*, ( SELECT COUNT(*) FROM FLW1200 Z WHERE 1=1 AND Z.PRJ_ID = B.PRJ_ID AND Z.PROCESS_ID = B.PROCESS_ID AND Z.FLOW_ID = B.FLOW_ID ) AS OPT_CNT ,D.REQ_TOTAL_CNT ,D.REQ_CHARGER_CNT FROM FLW1100 B, FLW1000 C,( SELECT Z.PRJ_ID, Z.PROCESS_ID, Z.FLOW_ID , COUNT(*) AS REQ_TOTAL_CNT , COUNT(DECODE(Z.REQ_CHARGER_ID,#usrId#,1)) AS REQ_CHARGER_CNT FROM REQ4100 Z WHERE 1=1 AND Z.PRJ_ID = #prjId# GROUP BY Z.PRJ_ID,Z.PROCESS_ID,Z.FLOW_ID ) D WHERE 1=1 AND B.PRJ_ID = C.PRJ_ID AND B.PROCESS_ID = C.PROCESS_ID AND C.USE_CD = '01' AND C.PROCESS_CONFIRM_CD = '02' AND B.PRJ_ID = #prjId# AND B.USE_CD = '01' AND B.PRJ_ID = D.PRJ_ID(+) AND B.PROCESS_ID = D.PROCESS_ID(+) AND B.FLOW_ID = D.FLOW_ID(+)
AND B.PROCESS_ID = #processId#
) A START WITH A.FLOW_NEXT_ID IS NULL CONNECT BY PRIOR A.FLOW_ID = A.FLOW_NEXT_ID AND PRIOR A.PROCESS_ID = A.PROCESS_ID ORDER BY A.PRJ_ID, A.PROCESS_ID,LEVEL DESC

SELECT B.PROCESS_ID, B.OVER_TYPE, COUNT(*) AS OVER_CNT FROM ( SELECT CASE A.REQ_PRO_TYPE WHEN '04' THEN CASE WHEN TO_DATE(TO_CHAR(A.REQ_ED_DU_DTM,'YYYY-MM-DD'),'YYYY-MM-DD') < TO_DATE(TO_CHAR(NVL(REQ_ED_DTM,(CURRENT_TIMESTAMP+0)),'YYYY-MM-DD'),'YYYY-MM-DD') THEN '04' ELSE '05' END ELSE CASE WHEN TO_DATE(TO_CHAR(A.REQ_ED_DU_DTM,'YYYY-MM-DD'),'YYYY-MM-DD') < TO_DATE(TO_CHAR(NVL(REQ_ED_DTM,(CURRENT_TIMESTAMP+0)),'YYYY-MM-DD'),'YYYY-MM-DD') THEN '03' WHEN TO_DATE(TO_CHAR(A.REQ_ED_DU_DTM-3,'YYYY-MM-DD'),'YYYY-MM-DD') < TO_DATE(TO_CHAR(NVL(REQ_ED_DTM,(CURRENT_TIMESTAMP+0)),'YYYY-MM-DD'),'YYYY-MM-DD') THEN '02' ELSE '01' END END AS OVER_TYPE ,A.PROCESS_ID FROM REQ4100 A WHERE 1=1 AND A.REQ_PRO_TYPE != '01' AND A.REQ_PRO_TYPE != '05' AND A.REQ_PRO_TYPE != '06' AND A.PROCESS_ID IS NOT NULL AND A.PRJ_ID = #prjId#
AND A.PROCESS_ID = #processId#
) B GROUP BY B.PROCESS_ID, B.OVER_TYPE ORDER BY B.PROCESS_ID, B.OVER_TYPE
