sqlMap.select:
SELECT Z.*, CONNECT_BY_ISLEAF AS LEAF FROM( SELECT A.PRJ_ID , A.PRJ_GRP_ID , NVL(A.PRJ_GRP_ID, '000') AS UPPER_ID , A.LIC_GRP_ID , A.PRJ_NM , TO_CHAR(TO_DATE(A.START_DT,'YYYYMMDD'),'YYYY-MM-DD') AS START_DT , TO_CHAR(TO_DATE(A.END_DT,'YYYYMMDD'),'YYYY-MM-DD') AS END_DT , A.ORD , A.PRJ_DESC , A.USE_CD , A.PRJ_TYPE , SF_ADM4100_COM_CD_INFO(#licGrpId#, 'PRJ00013', A.PRJ_TYPE, '1') AS PRJ_TYPE_NM , A.PRJ_ACRM , A.PRJ_GRP_CD , A.REG_DTM , A.REG_USR_ID , A.REG_USR_IP , A.MODIFY_DTM , A.MODIFY_USR_ID , A.MODIFY_USR_IP FROM PRJ1000 A WHERE 1=1 AND A.LIC_GRP_ID = #licGrpId# AND A.USE_CD = '01'
AND A.PRJ_GRP_CD = '01'
) Z CONNECT BY PRIOR Z.PRJ_ID = Z.PRJ_GRP_ID START WITH Z.PRJ_GRP_ID IS NULL ORDER SIBLINGS BY Z.ORD, Z.PRJ_NM
