sqlMap.select:
SELECT B.PRJ_ID , C.PRJ_NM , B.AUTH_GRP_ID , B.AUTH_GRP_NM , NVL(B.AUTH_GRP_DESC, ' ') AS AUTH_GRP_DESC , B.CREATE_DT , B.USE_CD , B.ORD , B.USR_TYP , SF_ADM4100_COM_CD_INFO(#licGrpId#, 'ADM00004', B.USR_TYP, '1') AS USR_TYP_NM , SF_ADM4100_COM_CD_INFO(#licGrpId#, 'CMM00001', B.ACCEPT_USE_CD, '1') AS ACCEPT_USE_NM FROM ( SELECT DISTINCT Z.PRJ_ID FROM ADM1100 Z, ADM1300 X WHERE 1=1 AND Z.PRJ_ID = X.PRJ_ID AND Z.AUTH_GRP_ID = X.AUTH_GRP_ID AND Z.USR_TYP = '02' AND Z.LIC_GRP_ID = #licGrpId#
AND USR_ID = #usrId#
) A, ADM1100 B, PRJ1000 C WHERE 1=1 AND A.PRJ_ID = B.PRJ_ID AND A.PRJ_ID = C.PRJ_ID AND C.USE_CD = '01' AND B.USE_CD = '01' ORDER BY B.PRJ_ID, B.ORD
